mapper.insert:
INSERT INTO USERS
VALUES
(
	 #{userId}
	,#{name}
	,#{alias}
	,#{pass}
	,#{addr1}
	,#{addr2}
	,#{zipcd}
	,#{birth, jdbcType=DATE}
	,#{path, jdbcType=VARCHAR}
	,#{filename, jdbcType=VARCHAR}
)

mapper.update:
UPDATE USERS
SET  NAME	  = #{name}   	
	,ALIAS    = #{alias}     
	,PASS     = #{pass}     			
	,ADDR1    = #{addr1}    			
	,ADDR2    = #{addr2}    			
	,ZIPCD    = #{zipcd}    			
	,BIRTH    = #{birth, jdbcType=DATE}    			
	,PATH     = #{path}     			
	,FILENAME = #{filename} 	
WHERE USERID = #{userId}

UPDATE USERS
SET    PASS   = #{pass}
WHERE  USERID = #{userId}
